import React, { Component } from "react";
import ManagerCard from "./components/ManagerCard";
import EngineerCard from "./components/EngineerCard";
import InternCard from "./components/InternCard";
import Wrapper from "./components/Wrapper";
import Title from "./components/Title";
import employee from "./employee.json";

class App extends Component {
  // Setting this.state.employee to the employee json array
  state = {
    employee
  };

  removeEmployee = id => {
    // Filter this.state.employee for employee with an id not equal to the id being removed
    const employee = this.state.employee.filter(employee => employee.id !== id);
    // Set this.state.employee equal to the new employee array
    this.setState({ employee });
  };

  renderManager = () => {
    // finds the right thing but now needs to write onto it
    // maybe put the info in the comonents?
    let manager = this.state.employee.filter(employee => employee.occupation === "Restaurant Owner")
    console.log(manager[0])
    return <ManagerCard
      removeemployee={this.removeemployee}
      id={manager.id}
      key={manager.id}
      name={manager.name}
      image={manager.image}
      occupation={manager.occupation}
      location={manager.location}
      officeNum={manager.officeNum} />;

  };

  renderEngineer = () => {
    // finds the right thing but now needs to write onto it
    // maybe put the info in the comonents?
    if (this.state.employee.map(employee => employee.occupation === "Cashier")) {
      return <EngineerCard />;
    }
  };

  renderIntern = () => {
    // finds the right thing but now needs to write onto it
    // maybe put the info in the comonents?
    if (this.state.employee.map(employee => employee.occupation === "Fry Cook")) {
      return <InternCard />;
    }
  };

  // Needs a short circuit for each card to only render the employee type
  render() {
    return (
      <Wrapper>
        <Title>Employee List</Title>
        {this.state.employee.map(employee => (
          <ManagerCard
            removeemployee={this.removeemployee}
            id={employee.id}
            key={employee.id}
            name={employee.name}
            image={employee.image}
            occupation={employee.occupation}
            location={employee.location}
            officeNum={employee.officeNum}
          />
        ))}
        {this.state.employee.map(employee => (
          <EngineerCard
            removeemployee={this.removeemployee}
            id={employee.id}
            key={employee.id}
            name={employee.name}
            image={employee.image}
            occupation={employee.occupation}
            location={employee.location}
            githubURL={employee.githubURL}
          />
        ))}
        {this.state.employee.map(employee => (
          <InternCard
            removeemployee={this.removeemployee}
            id={employee.id}
            key={employee.id}
            name={employee.name}
            image={employee.image}
            occupation={employee.occupation}
            location={employee.location}
            school={employee.school}
          />
        ))}
      </Wrapper>
    );
  }
}

export default App;


